INSERT INTO `npc` VALUES ('2147483782', '{\"id\":2147483782,\"name\":\"Mage Sage\",\"file\":15,\"map\":7,\"point\":{\"x\":1040,\"y\":1152},\"direction\":7,\"action\":[{\"condition\":{\"type\":\"level\",\"max\":1},\"type\":\"exp\",\"amount\":1000},{\"type\":\"npcSay\",\"message\":\"I am the #rMage Sage#w, I can teach you the basics of being a Mage.#eWhat would you like to learn?\",\"options\":[{\"text\":\"#gTell me about the Mage class.\",\"action\":{\"type\":\"array\",\"actions\":[{\"type\":\"npcSay\",\"message\":\"The Mage is a class that specializes in the use of magic. They possess weaker physical attributes, but their magical abilities can devastate their enemies. Rely on hearty tank pets or Centaurs to keep them alive.\"}]}},{\"text\":\"#gTell me about Mage Stats.\",\"action\":{\"type\":\"npcSay\",\"message\":\"INT is the most important stat for a Mage. It increases the amount of mana you have, as well as the damage of your spells. AGI can also prove useful, allowing you to cast before your opponents.\"}},{\"text\":\"#gI\'ve heard enough.\",\"action\":{\"type\":\"array\",\"actions\":[{\"type\":\"npcSay\",\"message\":\"Apply stat points from the Character Status window (#yAlt+W#w or left click the character avatar in the top left of the screen) and then I believe you\'re ready to challenge the Battle Instructor!\"},{\"type\":\"quest\",\"set\":{\"quest\":1001,\"stage\":1}}]}}]}]}');
