INSERT INTO `npc` VALUES ('2147483784', '{\"id\":2147483784,\"name\":\"Pet Keeper\",\"file\":131,\"map\":7,\"point\":{\"x\":1152,\"y\":672},\"direction\":5,\"type\":2,\"action\":[{\"condition\":{\"type\":\"level\",\"min\":2},\"type\":\"npcSay\",\"message\":\"I am the #rPet Keeper#w. Give me a Pet Egg to hatch a pet. Use #wAlt+G#w or click the give icon on the hotbar, then click on me and select the Pet Egg.\"},{\"condition\":[{\"type\":\"hasPet\"},{\"type\":\"quest\",\"quest\":1002,\"stage\":0}],\"type\":\"array\",\"actions\":[{\"type\":\"npcSay\",\"message\":\"I have hatched your pet. Now you can set it to active to fight alongside you in battle. Open the Pet window with either #yAlt+P#w or left clicking the pawprint icon undearneath the player avatar in the top left of the screen. Select the #rBloodpede#w and the press #yActive#w.\",\"onClose\":{\"type\":\"quest\",\"set\":{\"quest\":1002,\"stage\":2}}}]},{\"condition\":[{\"type\":\"petLevel\",\"min\":1},{\"type\":\"quest\",\"quest\":1002,\"stage\":2}],\"type\":\"array\",\"actions\":[{\"type\":\"npcSay\",\"message\":\"Congratulations on your new pet! I hope you treat it well.\"},{\"type\":\"quest\",\"remove\":1002},{\"type\":\"exp\",\"amount\":2125,\"pet\":true}]},{\"condition\":{\"type\":\"quest\",\"quest\":1002,\"not\":true},\"type\":\"npcSay\",\"message\":\"With your new pet in tow, go and seek the Instructor near the Teleporter to get your first armor and next steps.\"}]}');
INSERT INTO `npc` VALUES ('2147483785', '{\"id\":2147483785,\"name\":\"Borg Sage\",\"file\":17,\"map\":7,\"point\":{\"x\":2080,\"y\":1328},\"direction\":7,\"action\":[{\"condition\":{\"type\":\"level\",\"max\":1},\"type\":\"exp\",\"amount\":1000},{\"type\":\"npcSay\",\"message\":\"I am the #rBorg Sage#w, I can teach you the basics of being a Borg.#eWhat would you like to learn?\",\"options\":[{\"text\":\"#gTell me about the Borg class.\",\"action\":{\"type\":\"array\",\"actions\":[{\"type\":\"npcSay\",\"message\":\"The borg is a very versatile class. They can play both offensively: draining enemies HP and MP simultaneously or using powerful melee attacks, or defensively: applying buffs to themselves and teammates.\"}]}},{\"text\":\"#gTell me about Borg Stats.\",\"action\":{\"type\":\"npcSay\",\"message\":\"Borgs excel at STR to bolster their melee damage. They can also benefit from high AGI allowing them to apply buffs or drain enemies HP and MP quickly, or even STA to protect allies.\"}},{\"text\":\"#gI\'ve heard enough.\",\"action\":{\"type\":\"array\",\"actions\":[{\"type\":\"npcSay\",\"message\":\"Apply stat points from the Character Status window (#yAlt+W#w or left click the character avatar in the top left of the screen) and then I believe you\'re ready to challenge the Battle Instructor!\"},{\"type\":\"quest\",\"set\":{\"quest\":1001,\"stage\":1}}]}}]}]}');
INSERT INTO `npc` VALUES ('2147483792', '{\"id\":2147483792,\"name\":\"Human Sage\",\"file\":11,\"map\":7,\"point\":{\"x\":2304,\"y\":1872},\"direction\":5,\"action\":[{\"condition\":{\"type\":\"level\",\"max\":1},\"type\":\"exp\",\"amount\":1000},{\"type\":\"npcSay\",\"message\":\"I am the #rHuman Sage#w, I can teach you the basics of being a Human.#eWhat would you like to learn?\",\"options\":[{\"text\":\"#gTell me about the Human class.\",\"action\":{\"type\":\"array\",\"actions\":[{\"type\":\"npcSay\",\"message\":\"The primary objective for a Human is to take control of the battle flow by applying Chaos and Hypno to their enemies. Applying Poison (Female only) or Frailty (Male only) to further weaken their opponents. Stun is effective as a last resort to prevent an enemy taking a turn however whilst stunned they are impervious to any damage.\"}]}},{\"text\":\"#gTell me about Human Stats.\",\"action\":{\"type\":\"npcSay\",\"message\":\"Humans primary stat is the STA stat, increasing the maximum HP. INT also applies a small bonus to control skills such as Chaos and Hypno.\"}},{\"text\":\"#gI\'ve heard enough.\",\"action\":{\"type\":\"array\",\"actions\":[{\"type\":\"npcSay\",\"message\":\"Apply stat points from the Character Status window (#yAlt+W#w or left click the character avatar in the top left of the screen) and then I believe you\'re ready to challenge the Battle Instructor!\"},{\"type\":\"quest\",\"set\":{\"quest\":1001,\"stage\":1}}]}}]}]}');
