INSERT INTO `npc` VALUES ('2147487744', '{\"id\":2147487744,\"name\":\"Alice\",\"direction\":7,\"file\":104,\"map\":1,\"point\":{\"x\":8200,\"y\":3300},\"action\":[{\"type\":\"npcSay\",\"message\":\"Hi, wanna help me out with something?\",\"options\":[{\"text\":\"#GYeah sure#N\",\"action\":{\"type\":\"npcSay\",\"message\":[\"So, I was visiting Bob the other day, and oh dear his place is dusty! Turns out he lost his broom, and I told him he could have my old one.\",\"Unfortunately, I\'m way too busy to go visit him again, so could you bring it to him?\"],\"options\":[{\"text\":\"#GSure, I got nothing better to do...#N\",\"action\":{\"type\":\"npcSay\",\"message\":\"Thank you so much! You can find him outside the Herbal Shop.\",\"onClose\":[{\"type\":\"quest\",\"add\":1000},{\"type\":\"addItem\",\"baseItemId\":1000}]}},{\"text\":\"#YNo, I\'m deadly allergic to brooms.#N\",\"action\":{\"type\":\"npcSay\",\"message\":\"You are? Well okay then, I\'ll find someone else.\"}}]}},{\"text\":\"#YNo, I\'m kinda busy.#N\",\"action\":{\"type\":\"npcSay\",\"message\":\"Well, come back if you change your mind!\"}}],\"condition\":{\"type\":\"quest\",\"quest\":1000,\"not\":true},\"else\":{\"type\":\"npcSay\",\"message\":\"Did you bring the broom to Bob yet?\"}}]}');
INSERT INTO `npc` VALUES ('2147487745', '{\"id\":2147487745,\"name\":\"Bob\",\"file\":101,\"map\":1,\"point\":{\"x\":6408,\"y\":4871},\"direction\":5,\"action\":{\"type\":\"npcSay\",\"message\":\"~Z2Achoo#E #EOh dear, all this dust is making me sneeze.\",\"options\":[{\"text\":\"#GYeah, Alice asked me to bring you this broom...#E\",\"condition\":[{\"type\":\"quest\",\"quest\":1000,\"stage\":0},{\"type\":\"item\",\"baseItemId\":1000}],\"action\":{\"type\":\"npcSay\",\"message\":[\"Oh thank you very much!\",\"Wait a minute, this broom is broken!\",\"Why don\'t you ask Charlie to fix it, I\'ll totes reward you for it.\"],\"onClose\":{\"type\":\"quest\",\"set\":{\"quest\":1000,\"stage\":1}}}},{\"text\":\"#GLook, this broom is all fixed up!#E\",\"condition\":[{\"type\":\"quest\",\"quest\":1000,\"stage\":3},{\"type\":\"item\",\"baseItemId\":1002}],\"action\":{\"type\":\"npcSay\",\"message\":\"Hurray! Now I can finally get rid of all this dust!\",\"onClose\":[{\"type\":\"removeItem\",\"baseItemId\":1002},{\"type\":\"quest\",\"remove\":1000},{\"type\":\"exp\",\"amount\":10000},{\"type\":\"gold\",\"amount\":10000}]}}]}}');
INSERT INTO `npc` VALUES ('2147487746', '{\"id\":2147487746,\"name\":\"Charlie\",\"direction\":7,\"file\":129,\"map\":1,\"point\":{\"x\":8110,\"y\":6688},\"action\":{\"type\":\"npcSay\",\"message\":\"Is this world real? Am I real?\",\"options\":[{\"text\":\"#GUh, can you fix this broom?#E\",\"condition\":[{\"type\":\"quest\",\"quest\":1000,\"stage\":1},{\"type\":\"item\",\"baseItemId\":1000}],\"action\":{\"type\":\"npcSay\",\"message\":\"Ah yes, this broom reminds me of my grandmother, but it require 5 leaves.\",\"options\":[{\"text\":\"#GWhy do you need leaves to fix a broom?#E\",\"action\":{\"type\":\"npcSay\",\"message\":\"Because I couldn\'t find a more appropriate image in the game files. Now go, you can get them from monsters in Revive Arena!\",\"onClose\":{\"type\":\"quest\",\"set\":{\"quest\":1000,\"stage\":2}}}}]}},{\"text\":\"#GSure.. I got 5 leaves for you.#E\",\"condition\":[{\"type\":\"quest\",\"quest\":1000,\"stage\":2},{\"type\":\"item\",\"baseItemId\":1000},{\"type\":\"item\",\"baseItemId\":1001,\"count\":5}],\"action\":{\"type\":\"npcSay\",\"message\":\"Great, here\'s your fixed broom!\",\"onClose\":[{\"type\":\"removeItem\",\"baseItemId\":1000},{\"type\":\"removeItem\",\"baseItemId\":1001,\"amount\":5},{\"type\":\"addItem\",\"baseItemId\":1002},{\"type\":\"quest\",\"set\":{\"quest\":1000,\"stage\":3}}]}}]}}');
